import { useEffect, useState } from "react";

function Product() {
  const [products, setProducts] = useState([]);
  const [categories, setCategories] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState("");

  const [form, setForm] = useState({
    name: "",
    description: "",
    imageUrl: "",
    price: "",
    category: "",
  });

  const [newCategoryName, setNewCategoryName] = useState("");

  // Fetch products and categories on mount
  useEffect(() => {
    fetchProducts();
    fetchCategories();
  }, []);

  const fetchProducts = () => {
    fetch("http://localhost:5000/api/products")
      .then((res) => res.json())
      .then(setProducts)
      .catch((err) => console.error("Failed to fetch products:", err));
  };

  const fetchCategories = () => {
    fetch("http://localhost:5000/api/categories")
      .then((res) => res.json())
      .then(setCategories)
      .catch((err) => console.error("Failed to fetch categories:", err));
  };

  // Add a new product
  const addProduct = async (e) => {
    e.preventDefault();

    if (!form.category) {
      alert("Please select a category");
      return;
    }

    const res = await fetch("http://localhost:5000/api/products", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(form),
    });

    if (res.ok) {
      const newProduct = await res.json();
      setProducts([...products, newProduct]);
      setForm({
        name: "",
        description: "",
        imageUrl: "",
        price: "",
        category: "",
      });
    } else {
      const errorData = await res.json();
      alert(errorData.message || "Failed to add product");
    }
  };

  // Add a new category
  const addCategory = async (e) => {
    e.preventDefault();
    if (!newCategoryName.trim()) {
      alert("Category name can't be empty");
      return;
    }
    const res = await fetch("http://localhost:5000/api/categories", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ name: newCategoryName }),
    });
    if (res.ok) {
      const category = await res.json();
      setCategories([...categories, category]);
      setNewCategoryName("");
    } else {
      const errorData = await res.json();
      alert(errorData.message || "Failed to add category");
    }
  };

  const deleteProduct = async (id) => {
    if (!window.confirm("Are you sure?")) return;
    await fetch(`http://localhost:5000/api/products/${id}`, {
      method: "DELETE",
    });
    setProducts(products.filter((p) => p._id !== id));
  };

  return (
    <div className="p-6 max-w-4xl mx-auto">
      <h1 className="text-2xl font-bold mb-4">Add Category</h1>
      <form onSubmit={addCategory} className="flex mb-6 gap-2">
        <input
          type="text"
          placeholder="New Category Name"
          value={newCategoryName}
          onChange={(e) => setNewCategoryName(e.target.value)}
          className="flex-grow p-2 border rounded"
        />
        <button
          type="submit"
          className="bg-green-600 text-white px-4 py-2 rounded"
        >
          Add Category
        </button>
      </form>

      <h1 className="text-2xl font-bold mb-4">Add Product</h1>
      <form onSubmit={addProduct} className="space-y-2">
        <input
          type="text"
          value={form.name}
          placeholder="Name"
          onChange={(e) => setForm({ ...form, name: e.target.value })}
          className="w-full p-2 border rounded"
          required
        />
        <input
          type="text"
          value={form.description}
          placeholder="Description"
          onChange={(e) => setForm({ ...form, description: e.target.value })}
          className="w-full p-2 border rounded"
          required
        />
        <input
          type="text"
          value={form.imageUrl}
          placeholder="Image URL"
          onChange={(e) => setForm({ ...form, imageUrl: e.target.value })}
          className="w-full p-2 border rounded"
        />
        <input
          type="number"
          value={form.price}
          placeholder="Price"
          onChange={(e) => setForm({ ...form, price: e.target.value })}
          className="w-full p-2 border rounded"
          required
          min="0"
          step="0.01"
        />
        <select
          value={form.category}
          onChange={(e) => setForm({ ...form, category: e.target.value })}
          className="w-full p-2 border rounded"
          required
        >
          <option value="">Select Category</option>
          {categories.map((cat) => (
            <option key={cat._id} value={cat._id}>
              {cat.name}
            </option>
          ))}
        </select>
        <button className="bg-blue-600 text-white px-4 py-2 rounded">
          Add Product
        </button>
      </form>

      <h2 className="text-xl font-semibold mt-6">Filter Products</h2>
      <select
        onChange={(e) => setSelectedCategory(e.target.value)}
        className="w-full p-2 border rounded mb-4"
        value={selectedCategory}
      >
        <option value="">All</option>
        {categories.map((cat) => (
          <option key={cat._id} value={cat._id}>
            {cat.name}
          </option>
        ))}
      </select>

      <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
        {products
          .filter(
            (p) => !selectedCategory || p.category?._id === selectedCategory
          )
          .map((p) => (
            <div key={p._id} className="border p-4 rounded">
              <img
                src={p.imageUrl}
                alt={p.name}
                className="w-full h-40 object-cover mb-2"
              />
              <h3 className="font-bold text-lg">{p.name}</h3>
              <p>{p.description}</p>
              <p>${p.price}</p>
              <p className="text-sm text-gray-500">{p.category?.name}</p>
              <button
                onClick={() => deleteProduct(p._id)}
                className="mt-2 bg-red-500 text-white px-3 py-1 rounded"
              >
                Delete
              </button>
            </div>
          ))}
      </div>
    </div>
  );
}

export default Product;
