// ==== React Component (ProductManager.jsx) ====
import { useEffect, useState } from "react";

function Product() {
  const [products, setProducts] = useState([]);
  const [categories, setCategories] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState("");

  const [formData, setFormData] = useState({
    name: "",
    description: "",
    imageUrl: "",
    price: "",
    category: "",
  });

  useEffect(() => {
    fetch("http://localhost:5000/api/products")
      .then((res) => res.json())
      .then(setProducts);

    fetch("http://localhost:5000/api/categories")
      .then((res) => res.json())
      .then(setCategories);
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const res = await fetch("http://localhost:5000/api/products", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(formData),
    });
    if (res.ok) {
      const newProd = await res.json();
      setProducts((prev) => [...prev, newProd]);
      setFormData({ name: "", description: "", imageUrl: "", price: "", category: "" });
    }
  };

  const handleDelete = async (id) => {
    if (!window.confirm("Delete this product?")) return;
    await fetch(`http://localhost:5000/api/products/${id}`, { method: "DELETE" });
    setProducts(products.filter((p) => p._id !== id));
  };

  return (
    <div className="p-6">
      <h1 className="text-xl font-bold">Add Product</h1>
      <form onSubmit={handleSubmit} className="space-y-2">
        <input value={formData.name} onChange={(e) => setFormData({ ...formData, name: e.target.value })} placeholder="Name" className="border p-2 w-full" />
        <input value={formData.description} onChange={(e) => setFormData({ ...formData, description: e.target.value })} placeholder="Description" className="border p-2 w-full" />
        <input value={formData.imageUrl} onChange={(e) => setFormData({ ...formData, imageUrl: e.target.value })} placeholder="Image URL" className="border p-2 w-full" />
        <input type="number" value={formData.price} onChange={(e) => setFormData({ ...formData, price: e.target.value })} placeholder="Price" className="border p-2 w-full" />
        <select value={formData.category} onChange={(e) => setFormData({ ...formData, category: e.target.value })} className="border p-2 w-full">
          <option value="">Select Category</option>
          {categories.map((cat) => (
            <option key={cat._id} value={cat._id}>{cat.name}</option>
          ))}
        </select>
        <button type="submit" className="bg-blue-500 text-white px-4 py-2 rounded">Add</button>
      </form>

      <div className="mt-6">
        <h2 className="text-lg font-semibold">Filter by Category</h2>
        <select onChange={(e) => setSelectedCategory(e.target.value)} className="border p-2">
          <option value="">All</option>
          {categories.map((cat) => (
            <option key={cat._id} value={cat._id}>{cat.name}</option>
          ))}
        </select>

        <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mt-4">
          {products
            .filter(p => !selectedCategory || p.category?._id === selectedCategory)
            .map(prod => (
              <div key={prod._id} className="border p-4 rounded shadow">
                <img src={prod.imageUrl} alt={prod.name} className="h-40 w-full object-cover mb-2" />
                <h3 className="font-bold">{prod.name}</h3>
                <p>{prod.description}</p>
                <p className="text-sm text-gray-600">${prod.price}</p>
                <p className="text-xs text-gray-400">{prod.category?.name}</p>
                <button onClick={() => handleDelete(prod._id)} className="mt-2 bg-red-500 text-white px-3 py-1 rounded">Delete</button>
              </div>
            ))}
        </div>
      </div>
    </div>
  );
}

export default Product;
