// App.jsx
import React, { useState, useEffect } from 'react';

function App() {
  const [cards, setCards] = useState([]);
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');

  // Fetch cards
  useEffect(() => {
    fetch('http://localhost:5000/api/cards')
      .then(res => res.json())
      .then(data => setCards(data));
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!title || !description) return alert('Please fill all fields');

    const res = await fetch('http://localhost:5000/api/add/card', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ title, description }),
    });

    if (res.ok) {
      const newCard = await res.json();
      setCards(prev => [...prev, newCard]);
      setTitle('');
      setDescription('');
    } else {
      alert('Failed to add card');
    }
  };

  return (
    <div className="max-w-xl mx-auto p-4">
      <h1 className="text-2xl font-bold mb-4">Add a Card</h1>
      <form onSubmit={handleSubmit} className="mb-6 space-y-4">
        <input
          type="text"
          placeholder="Title"
          className="w-full p-2 border rounded"
          value={title}
          onChange={e => setTitle(e.target.value)}
        />
        <textarea
          placeholder="Description"
          className="w-full p-2 border rounded"
          value={description}
          onChange={e => setDescription(e.target.value)}
        />
        <button
          type="submit"
          className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
        >
          Add Card
        </button>
      </form>

      <h2 className="text-xl font-semibold mb-3">Cards</h2>
      <div className="space-y-4">
        {cards.length === 0 && <p>No cards yet.</p>}
        {cards.map(card => (
          <div key={card._id} className="border p-4 rounded shadow">
            <img src={card}/>
            <h3 className="font-bold text-lg">{card.title}</h3>
            <p>{card.description}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
