// / === Login.jsx ===
import { useState } from "react";

function Login({ onLogin }) {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [role, setRole] = useState("user");

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await fetch("http://localhost:5000/api/login", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ username, password, role }),
      });
      const data = await res.json();
      if (!res.ok) return alert(data.message || "Login failed");
      onLogin(data);
    } catch (err) {
      alert("Login error");
    }
  };

  return (
    <div className="flex items-center justify-center h-screen bg-gray-100">
      <form onSubmit={handleSubmit} className="space-y-6 bg-white p-6 rounded-xl shadow-xl w-80">
        <h2 className="text-2xl font-bold text-center">Login</h2>
        <input placeholder="Username" value={username} onChange={(e) => setUsername(e.target.value)} required className="w-full p-3 border rounded" />
        <input placeholder="Password" type="password" value={password} onChange={(e) => setPassword(e.target.value)} required className="w-full p-3 border rounded" />
        <select value={role} onChange={(e) => setRole(e.target.value)} className="w-full p-3 border rounded">
          <option value="user">User</option>
          <option value="admin">Admin</option>
        </select>
        <button type="submit" className="w-full bg-blue-600 text-white p-3 rounded">Login / Register</button>
      </form>
    </div>
  );
}

export default Login;
