import { useEffect, useState } from 'react';

function App() {
  const [cards, setCards] = useState([]);
  const [formData, setFormData] = useState({
    title: '',
    imageUrl: '',
    downloadUrl: '',
    readUrl: '',
  });

  const fetchCards = () => {
    fetch('/api/cards')
      .then(res => res.json())
      .then(data => setCards(data));
  };

  useEffect(() => {
    fetchCards();
  }, []);

  const handleChange = e => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = e => {
    e.preventDefault();
    fetch('/api/cards', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(formData)
    })
      .then(res => res.json())
      .then(data => {
        setFormData({ title: '', imageUrl: '', downloadUrl: '', readUrl: '' });
        fetchCards(); // refresh cards
      });
  };

  return (
    <div className="p-4">
      {/* Form */}
      <form onSubmit={handleSubmit} className="mb-8 grid gap-4 max-w-md mx-auto">
        <input
          name="title"
          value={formData.title}
          onChange={handleChange}
          placeholder="Title"
          className="p-2 border rounded"
          required
        />
        <input
          name="imageUrl"
          value={formData.imageUrl}
          onChange={handleChange}
          placeholder="Image URL"
          className="p-2 border rounded"
          required
        />
        <input
          name="downloadUrl"
          value={formData.downloadUrl}
          onChange={handleChange}
          placeholder="Download URL"
          className="p-2 border rounded"
          required
        />
        <input
          name="readUrl"
          value={formData.readUrl}
          onChange={handleChange}
          placeholder="Read URL"
          className="p-2 border rounded"
          required
        />
        <button type="submit" className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600">
          Add Card
        </button>
      </form>

      {/* Cards */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        {cards.map(card => (
          <div key={card._id} className="bg-white rounded-2xl shadow-lg p-4">
            <img src={card.imageUrl} alt={card.title} className="rounded-xl w-full h-48 object-cover" />
            <h2 className="text-xl font-semibold mt-4">{card.title}</h2>
            <div className="mt-4 flex justify-between">
              <a href={card.downloadUrl} className="bg-blue-500 text-white px-4 py-2 rounded-xl hover:bg-blue-600">Download</a>
              <a href={card.readUrl} className="bg-green-500 text-white px-4 py-2 rounded-xl hover:bg-green-600">Read</a>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
