import { useEffect, useState } from "react";

function Product() {
  const [products, setProducts] = useState([]);
  const [categories, setCategories] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState("");

  const [form, setForm] = useState({
    name: "",
    description: "",
    imageUrl: "",
    price: "",
    category: "",
  });

  const [newCategoryName, setNewCategoryName] = useState("");
  const [showCategoryInput, setShowCategoryInput] = useState(false);
  const [editingProductId, setEditingProductId] = useState(null);

  useEffect(() => {
    fetch("http://localhost:5000/api/products")
      .then((res) => res.json())
      .then(setProducts);

    fetch("http://localhost:5000/api/categories")
      .then((res) => res.json())
      .then(setCategories);
  }, []);

  const handleAddCategory = async () => {
    if (!newCategoryName.trim()) return alert("Please enter a category name.");

    const res = await fetch("http://localhost:5000/api/categories", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ name: newCategoryName }),
    });

    if (res.ok) {
      const newCategory = await res.json();
      setCategories([...categories, newCategory]);
      setForm({ ...form, category: newCategory._id });
      setShowCategoryInput(false);
      setNewCategoryName("");
    } else {
      const err = await res.json();
      alert(err.message || "Error adding category.");
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!form.category) return alert("Please select a category");

    const url = editingProductId
      ? `http://localhost:5000/api/products/${editingProductId}`
      : "http://localhost:5000/api/products";

    const method = editingProductId ? "PUT" : "POST";

    const res = await fetch(url, {
      method,
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(form),
    });

    if (res.ok) {
      const data = await res.json();
      if (editingProductId) {
        setProducts(products.map((p) => (p._id === data._id ? data : p)));
      } else {
        setProducts([...products, data]);
      }
      resetForm();
    }
  };

  const resetForm = () => {
    setForm({
      name: "",
      description: "",
      imageUrl: "",
      price: "",
      category: "",
    });
    setEditingProductId(null);
    setNewCategoryName("");
    setShowCategoryInput(false);
  };

  const handleDelete = async (id) => {
    if (!window.confirm("Are you sure?")) return;

    await fetch(`http://localhost:5000/api/products/${id}`, {
      method: "DELETE",
    });

    setProducts(products.filter((p) => p._id !== id));
  };

  const handleEdit = (product) => {
    setEditingProductId(product._id);
    setForm({
      name: product.name,
      description: product.description,
      imageUrl: product.imageUrl,
      price: product.price,
      category: product.category?._id || "",
    });
    setShowCategoryInput(false);
    setNewCategoryName("");
  };

  return (
    <div className="p-6">
      <h1 className="text-2xl font-bold mb-4">
        {editingProductId ? "Edit Product" : "Add Product"}
      </h1>
      <form onSubmit={handleSubmit} className="space-y-2">
        <input
          type="text"
          value={form.name}
          placeholder="Name"
          onChange={(e) => setForm({ ...form, name: e.target.value })}
          className="w-full p-2 border rounded"
          required
        />
        <input
          type="text"
          value={form.description}
          placeholder="Description"
          onChange={(e) => setForm({ ...form, description: e.target.value })}
          className="w-full p-2 border rounded"
          required
        />
        <input
          type="text"
          value={form.imageUrl}
          placeholder="Image URL"
          onChange={(e) => setForm({ ...form, imageUrl: e.target.value })}
          className="w-full p-2 border rounded"
          required
        />
        <input
          type="number"
          value={form.price}
          placeholder="Price"
          onChange={(e) => setForm({ ...form, price: e.target.value })}
          className="w-full p-2 border rounded"
          required
        />

        <select
          value={form.category}
          onChange={(e) => {
            if (e.target.value === "add_new") {
              setShowCategoryInput(true);
              setForm({ ...form, category: "" });
            } else {
              setForm({ ...form, category: e.target.value });
              setShowCategoryInput(false);
              setNewCategoryName("");
            }
          }}
          className="w-full p-2 border rounded"
          required
        >
          <option value="">Select Category</option>
          {categories.map((cat) => (
            <option key={cat._id} value={cat._id}>
              {cat.name}
            </option>
          ))}
          <option value="add_new">+ Add new category...</option>
        </select>

        {showCategoryInput && (
          <div className="flex gap-2">
            <input
              type="text"
              value={newCategoryName}
              onChange={(e) => setNewCategoryName(e.target.value)}
              placeholder="New Category Name"
              className="flex-1 p-2 border rounded"
            />
            <button
              type="button"
              onClick={handleAddCategory}
              className="bg-green-600 text-white px-4 py-2 rounded"
            >
              Save
            </button>
            <button
              type="button"
              onClick={() => {
                setShowCategoryInput(false);
                setNewCategoryName("");
              }}
              className="bg-gray-400 text-white px-4 py-2 rounded"
            >
              Cancel
            </button>
          </div>
        )}

        <div className="flex gap-2">
          <button
            type="submit"
            className="bg-blue-600 text-white px-4 py-2 rounded"
          >
            {editingProductId ? "Update Product" : "Add Product"}
          </button>
          {editingProductId && (
            <button
              type="button"
              onClick={resetForm}
              className="bg-gray-500 text-white px-4 py-2 rounded"
            >
              Cancel Edit
            </button>
          )}
        </div>
      </form>

      <h2 className="text-xl font-semibold mt-6">Filter Products</h2>
      <select
        onChange={(e) => setSelectedCategory(e.target.value)}
        className="w-full p-2 border rounded mb-4"
      >
        <option value="">All</option>
        {categories.map((cat) => (
          <option key={cat._id} value={cat._id}>
            {cat.name}
          </option>
        ))}
      </select>

      <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
        {products
          .filter(
            (p) => !selectedCategory || p.category?._id === selectedCategory
          )
          .map((p) => (
            <div key={p._id} className="border p-4 rounded">
              <img
                src={p.imageUrl}
                alt={p.name}
                className="w-full h-40 object-cover mb-2"
              />
              <h3 className="font-bold text-lg">{p.name}</h3>
              <p>{p.description}</p>
              <p>${p.price}</p>
              <p className="text-sm text-gray-500">{p.category?.name}</p>
              <div className="flex gap-2 mt-2">
                <button
                  onClick={() => handleEdit(p)}
                  className="bg-yellow-500 text-white px-3 py-1 rounded"
                >
                  Edit
                </button>
                <button
                  onClick={() => handleDelete(p._id)}
                  className="bg-red-500 text-white px-3 py-1 rounded"
                >
                  Delete
                </button>
              </div>
            </div>
          ))}
      </div>
    </div>
  );
}

export default Product;
