import { useState, useEffect } from "react";

function ProductManager() {
  const [products, setProducts] = useState([]);
  const [categories, setCategories] = useState([]);
  const [filter, setFilter] = useState("");

  const [form, setForm] = useState({
    title: "",
    imageUrl: "",
    downloadUrl: "",
    readUrl: "",
    description: "",
    category: "",
  });

  useEffect(() => {
    fetch("http://localhost:5000/api/categories")
      .then((res) => res.json())
      .then(setCategories);
  }, []);

  useEffect(() => {
    const url = filter
      ? `http://localhost:5000/api/products?category=${filter}`
      : "http://localhost:5000/api/products";
    fetch(url)
      .then((res) => res.json())
      .then(setProducts);
  }, [filter]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const res = await fetch("http://localhost:5000/api/products", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(form),
    });
    const data = await res.json();
    setProducts((prev) => [...prev, data]);
    setForm({
      title: "",
      imageUrl: "",
      downloadUrl: "",
      readUrl: "",
      description: "",
      category: "",
    });
  };

  return (
    <div className="p-6">
      <h1 className="text-2xl font-bold mb-4">Add Product</h1>
      <form onSubmit={handleSubmit} className="space-y-2">
        <input
          value={form.title}
          onChange={(e) => setForm({ ...form, title: e.target.value })}
          placeholder="Title"
          className="border p-2 w-full"
        />
        <input
          value={form.description}
          onChange={(e) => setForm({ ...form, description: e.target.value })}
          placeholder="Description"
          className="border p-2 w-full"
        />
        <input
          value={form.imageUrl}
          onChange={(e) => setForm({ ...form, imageUrl: e.target.value })}
          placeholder="Image URL"
          className="border p-2 w-full"
        />
        <input
          value={form.downloadUrl}
          onChange={(e) => setForm({ ...form, downloadUrl: e.target.value })}
          placeholder="Download URL"
          className="border p-2 w-full"
        />
        <input
          value={form.readUrl}
          onChange={(e) => setForm({ ...form, readUrl: e.target.value })}
          placeholder="Read URL"
          className="border p-2 w-full"
        />
        <select
          value={form.category}
          onChange={(e) => setForm({ ...form, category: e.target.value })}
          className="border p-2 w-full"
        >
          <option value="">Select Category</option>
          {categories.map((cat) => (
            <option key={cat._id} value={cat._id}>
              {cat.name}
            </option>
          ))}
        </select>
        <button className="bg-blue-600 text-white px-4 py-2 rounded">
          Submit
        </button>
      </form>

      <h2 className="text-xl font-semibold mt-6">Filter by Category</h2>
      <select
        onChange={(e) => setFilter(e.target.value)}
        className="border p-2 mb-4 w-full"
      >
        <option value="">All</option>
        {categories.map((cat) => (
          <option key={cat._id} value={cat._id}>
            {cat.name}
          </option>
        ))}
      </select>

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
        {products.map((product) => (
          <div key={product._id} className="border rounded p-4 shadow">
            <img
              src={product.imageUrl}
              alt={product.title}
              className="h-48 w-full object-cover"
            />
            <h3 className="font-bold text-lg mt-2">{product.title}</h3>
            <p>{product.description}</p>
            <p className="text-sm text-gray-500">
              Category: {product.category?.name}
            </p>
            <div className="flex gap-2 mt-2">
              <a
                href={product.downloadUrl}
                target="_blank"
                className="bg-green-600 text-white px-2 py-1 rounded"
              >
                Download
              </a>
              <a
                href={product.readUrl}
                target="_blank"
                className="bg-blue-600 text-white px-2 py-1 rounded"
              >
                Read
              </a>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default ProductManager;
