import { useEffect, useState } from "react";

function Product({ user }) {
  const isAdmin = user.role === "admin";

  const [products, setProducts] = useState([]);
  const [categories, setCategories] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState("");
  const [searchQuery, setSearchQuery] = useState("");
  const [page, setPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const [editingId, setEditingId] = useState(null);
  const [editingCategoryId, setEditingCategoryId] = useState(null);
  const [newCategory, setNewCategory] = useState("");
  const [oldPassword, setOldPassword] = useState("");
  const [newPassword, setNewPassword] = useState("");
  const [showPasswordForm, setShowPasswordForm] = useState(false);

  const [formData, setFormData] = useState({
    name: "",
    description: "",
    imageUrl: "",
    price: "",
    category: "",
  });

  useEffect(() => {
    fetch("http://localhost:5000/api/categories")
      .then((res) => res.json())
      .then(setCategories);
  }, []);

  useEffect(() => {
    const url = new URL("http://localhost:5000/api/products");
    if (selectedCategory) url.searchParams.append("category", selectedCategory);
    if (searchQuery) url.searchParams.append("search", searchQuery);
    url.searchParams.append("page", page);
    url.searchParams.append("limit", 9);

    fetch(url.toString())
      .then((res) => res.json())
      .then((data) => {
        setProducts(data.products);
        setTotalPages(data.totalPages);
      });
  }, [selectedCategory, searchQuery, page]);

  const refreshCategories = async () => {
    const res = await fetch("http://localhost:5000/api/categories");
    const data = await res.json();
    setCategories(data);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const method = editingId ? "PUT" : "POST";
    const endpoint = editingId
      ? `http://localhost:5000/api/products/${editingId}`
      : "http://localhost:5000/api/products";

    const res = await fetch(endpoint, {
      method,
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(formData),
    });

    if (res.ok) {
      setFormData({
        name: "",
        description: "",
        imageUrl: "",
        price: "",
        category: "",
      });
      setEditingId(null);
      setPage(1);
    }
  };

  const handleDelete = async (id) => {
    if (!window.confirm("Delete this product?")) return;
    await fetch(`http://localhost:5000/api/products/${id}`, {
      method: "DELETE",
    });
    setProducts((prev) => prev.filter((p) => p._id !== id));
  };

  const handleEdit = (prod) => {
    setFormData({
      name: prod.name,
      description: prod.description,
      imageUrl: prod.imageUrl,
      price: prod.price,
      category: prod.category?._id || "",
    });
    setEditingId(prod._id);
    window.scrollTo({ top: 0, behavior: "smooth" });
  };

  const handleAddCategory = async (e) => {
    e.preventDefault();
    if (!newCategory.trim()) return;

    const method = editingCategoryId ? "PUT" : "POST";
    const endpoint = editingCategoryId
      ? `http://localhost:5000/api/categories/${editingCategoryId}`
      : "http://localhost:5000/api/categories";

    const res = await fetch(endpoint, {
      method,
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ name: newCategory }),
    });

    if (res.ok) {
      await refreshCategories();
      setNewCategory("");
      setEditingCategoryId(null);
    }
  };

  const handleDeleteCategory = async (id) => {
    if (!window.confirm("Delete this category?")) return;
    const res = await fetch(`http://localhost:5000/api/categories/${id}`, {
      method: "DELETE",
    });

    const data = await res.json();
    if (res.ok) {
      await refreshCategories();
    } else {
      alert(data.message || "Could not delete category");
    }
  };

  return (
    <div className="p-6 bg-gray-50 min-h-screen">
      {/* Header */}
      <div className="flex justify-between items-center mb-8 p-4 bg-white rounded-xl shadow-sm">
        <h2 className="text-xl font-bold text-gray-800">
          Welcome, <span className="text-blue-600">{user.username}</span>
          <span className="text-sm ml-2 bg-blue-100 text-blue-800 px-2 py-1 rounded-full">
            {user.role}
          </span>
        </h2>
        
        <button
          onClick={() => window.location.reload()}
          className="text-red-500 hover:text-red-700 font-medium transition-colors"
        >
          Logout →
        </button>
      </div>

      {isAdmin && (
        <div className="space-y-8">
          {/* Category Section */}
          <div className="bg-white p-6 rounded-xl shadow-sm border border-gray-100">
            <h1 className="text-xl font-bold text-gray-800 mb-4">
              {editingCategoryId ? "✏️ Edit Category" : "➕ Add New Category"}
            </h1>
            <form onSubmit={handleAddCategory} className="space-y-3">
              <input
                required
                value={newCategory}
                onChange={(e) => setNewCategory(e.target.value)}
                placeholder="Category name"
                className="border border-gray-200 p-3 w-full rounded-lg focus:ring-2 focus:ring-blue-500 focus:outline-none transition-all"
              />
              <div className="flex gap-3">
                <button
                  type="submit"
                  className="bg-gradient-to-r from-green-500 to-green-600 text-white px-4 py-2 rounded-lg hover:shadow-md transition-all"
                >
                  {editingCategoryId ? "Update" : "Add"}
                </button>
                {editingCategoryId && (
                  <button
                    type="button"
                    onClick={() => {
                      setNewCategory("");
                      setEditingCategoryId(null);
                    }}
                    className="bg-gray-500 text-white px-4 py-2 rounded-lg hover:bg-gray-600 transition-colors"
                  >
                    Cancel
                  </button>
                )}
              </div>
            </form>
          </div>

          {/* Category List */}
          <div className="bg-white p-6 rounded-xl shadow-sm border border-gray-100">
            <h2 className="text-lg font-semibold text-gray-800 mb-4">
              📦 All Categories
            </h2>
            <ul className="divide-y divide-gray-200">
              {categories.map((cat) => (
                <li
                  key={cat._id}
                  className="py-3 flex items-center justify-between"
                >
                  <span className="font-medium">{cat.name}</span>
                  <div className="flex gap-2">
                    <button
                      onClick={() => {
                        setNewCategory(cat.name);
                        setEditingCategoryId(cat._id);
                      }}
                      className="bg-yellow-500 hover:bg-yellow-600 text-white px-3 py-1 rounded text-sm transition-colors"
                    >
                      Edit
                    </button>
                    <button
                      onClick={() => handleDeleteCategory(cat._id)}
                      className="bg-red-500 hover:bg-red-600 text-white px-3 py-1 rounded text-sm transition-colors"
                    >
                      Delete
                    </button>
                  </div>
                </li>
              ))}
            </ul>
          </div>

          {/* Product Form */}
          <div className="bg-white p-6 rounded-xl shadow-sm border border-gray-100">
            <h1 className="text-xl font-bold text-gray-800 mb-4">
              {editingId ? "✏️ Edit Product" : "➕ Add Product"}
            </h1>
            <form onSubmit={handleSubmit} className="space-y-4">
              <input
                required
                value={formData.name}
                onChange={(e) =>
                  setFormData({ ...formData, name: e.target.value })
                }
                placeholder="Name"
                className="border border-gray-200 p-3 w-full rounded-lg focus:ring-2 focus:ring-blue-500 focus:outline-none transition-all"
              />
              <input
                required
                value={formData.description}
                onChange={(e) =>
                  setFormData({ ...formData, description: e.target.value })
                }
                placeholder="Description"
                className="border border-gray-200 p-3 w-full rounded-lg focus:ring-2 focus:ring-blue-500 focus:outline-none transition-all"
              />
              <input
                required
                value={formData.imageUrl}
                onChange={(e) =>
                  setFormData({ ...formData, imageUrl: e.target.value })
                }
                placeholder="Image URL"
                className="border border-gray-200 p-3 w-full rounded-lg focus:ring-2 focus:ring-blue-500 focus:outline-none transition-all"
              />
              <input
                required
                type="number"
                value={formData.price}
                onChange={(e) =>
                  setFormData({ ...formData, price: e.target.value })
                }
                placeholder="Price"
                className="border border-gray-200 p-3 w-full rounded-lg focus:ring-2 focus:ring-blue-500 focus:outline-none transition-all"
              />
              <select
                required
                value={formData.category}
                onChange={(e) =>
                  setFormData({ ...formData, category: e.target.value })
                }
                className="border border-gray-200 p-3 w-full rounded-lg focus:ring-2 focus:ring-blue-500 focus:outline-none transition-all"
              >
                <option value="">Select Category</option>
                {categories.map((cat) => (
                  <option key={cat._id} value={cat._id}>
                    {cat.name}
                  </option>
                ))}
              </select>
              <div className="flex gap-3">
                <button
                  type="submit"
                  className="bg-gradient-to-r from-blue-500 to-blue-600 text-white px-4 py-2 rounded-lg hover:shadow-md transition-all"
                >
                  {editingId ? "Update" : "Add Product"}
                </button>
                {editingId && (
                  <button
                    type="button"
                    onClick={() => {
                      setFormData({
                        name: "",
                        description: "",
                        imageUrl: "",
                        price: "",
                        category: "",
                      });
                      setEditingId(null);
                    }}
                    className="bg-gray-500 hover:bg-gray-600 text-white px-4 py-2 rounded-lg transition-colors"
                  >
                    Cancel
                  </button>
                )}
              </div>
            </form>
          </div>
        </div>
      )}

      {/* Search & Filter */}
      <div className="mt-8 bg-white p-6 rounded-xl shadow-sm border border-gray-100">
        <h1 className="text-xl font-bold text-gray-800 mb-6">
          🔍 Filter Products
        </h1>
        <div className="flex flex-col md:flex-row gap-4">
          <select
            onChange={(e) => {
              setSelectedCategory(e.target.value);
              setPage(1);
            }}
            className="border border-gray-200 p-3 rounded-lg focus:ring-2 focus:ring-blue-500 focus:outline-none transition-all w-full md:w-64"
          >
            <option value="">All Categories</option>
            {categories.map((cat) => (
              <option key={cat._id} value={cat._id}>
                {cat.name}
              </option>
            ))}
          </select>
          <input
            required
            type="text"
            placeholder="Search products..."
            value={searchQuery}
            onChange={(e) => {
              setSearchQuery(e.target.value);
              setPage(1);
            }}
            className="border border-gray-200 p-3 rounded-lg focus:ring-2 focus:ring-blue-500 focus:outline-none transition-all w-full"
          />
        </div>

        {/* Product Grid */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mt-8">
          {products.map((prod) => (
            <div
              key={prod._id}
              className="border border-gray-200 rounded-xl overflow-hidden hover:shadow-md transition-shadow"
            >
              <img
                src={prod.imageUrl}
                alt={prod.name}
                className="h-48 w-full object-cover"
              />
              <div className="p-4">
                <h3 className="font-bold text-lg text-gray-800">{prod.name}</h3>
                <p className="text-gray-600 mt-1">{prod.description}</p>
                <p className="text-blue-600 font-medium mt-2">${prod.price}</p>
                <span className="inline-block bg-gray-100 text-gray-800 text-xs px-2 py-1 rounded-full mt-2">
                  {prod.category?.name}
                </span>
                {isAdmin && (
                  <div className="flex gap-2 mt-4">
                    <button
                      onClick={() => handleEdit(prod)}
                      className="bg-yellow-500 hover:bg-yellow-600 text-white px-3 py-1 rounded text-sm transition-colors"
                    >
                      Edit
                    </button>
                    <button
                      onClick={() => handleDelete(prod._id)}
                      className="bg-red-500 hover:bg-red-600 text-white px-3 py-1 rounded text-sm transition-colors"
                    >
                      Delete
                    </button>
                  </div>
                )}
              </div>
            </div>
          ))}
        </div>

        {/* Pagination */}
        <div className="flex justify-center mt-8 gap-2">
          <button
            disabled={page <= 1}
            onClick={() => setPage((p) => p - 1)}
            className="px-4 py-2 bg-gray-200 hover:bg-gray-300 rounded-lg disabled:opacity-50 transition-colors"
          >
            ← Prev
          </button>
          <span className="px-4 py-2 text-gray-700">
            Page {page} of {totalPages}
          </span>
          <button
            disabled={page >= totalPages}
            onClick={() => setPage((p) => p + 1)}
            className="px-4 py-2 bg-gray-200 hover:bg-gray-300 rounded-lg disabled:opacity-50 transition-colors"
          >
            Next →
          </button>
        </div>
      </div>
    </div>
  );
}

export default Product;
