import { useEffect, useState } from "react";

function Product() {
  const [products, setProducts] = useState([]);
  const [categories, setCategories] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState("");

  const [form, setForm] = useState({
    name: "",
    description: "",
    imageUrl: "",
    price: "",
    category: "",
  });

  const [newCategoryName, setNewCategoryName] = useState("");
  const [showCategoryInput, setShowCategoryInput] = useState(false);
  const [editingProductId, setEditingProductId] = useState(null);

  useEffect(() => {
    fetch("http://localhost:5000/api/products")
      .then((res) => res.json())
      .then(setProducts);

    fetch("http://localhost:5000/api/categories")
      .then((res) => res.json())
      .then(setCategories);
  }, []);

  const handleAddCategory = async () => {
    if (!newCategoryName.trim()) return alert("Please enter a category name.");

    const res = await fetch("http://localhost:5000/api/categories", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ name: newCategoryName }),
    });

    if (res.ok) {
      const newCategory = await res.json();
      setCategories([...categories, newCategory]);
      setForm({ ...form, category: newCategory._id });
      setShowCategoryInput(false);
      setNewCategoryName("");
    } else {
      const err = await res.json();
      alert(err.message || "Error adding category.");
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!form.category) return alert("Please select a category");

    const url = editingProductId
      ? `http://localhost:5000/api/products/${editingProductId}`
      : "http://localhost:5000/api/products";

    const method = editingProductId ? "PUT" : "POST";

    const res = await fetch(url, {
      method,
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(form),
    });

    if (res.ok) {
      const data = await res.json();
      if (editingProductId) {
        setProducts(products.map((p) => (p._id === data._id ? data : p)));
      } else {
        setProducts([...products, data]);
      }
      resetForm();
    }
  };

  const resetForm = () => {
    setForm({
      name: "",
      description: "",
      imageUrl: "",
      price: "",
      category: "",
    });
    setEditingProductId(null);
    setNewCategoryName("");
    setShowCategoryInput(false);
  };

  const handleDelete = async (id) => {
    if (!window.confirm("Are you sure?")) return;

    await fetch(`http://localhost:5000/api/products/${id}`, {
      method: "DELETE",
    });

    setProducts(products.filter((p) => p._id !== id));
  };

  const handleEdit = (product) => {
    setEditingProductId(product._id);
    setForm({
      name: product.name,
      description: product.description,
      imageUrl: product.imageUrl,
      price: product.price,
      category: product.category?._id || "",
    });
    setShowCategoryInput(false);
    setNewCategoryName("");
  };

  return (
    <div className="max-w-6xl mx-auto px-4 py-8">
      <h1 className="text-3xl font-bold mb-6 text-center text-gray-800">
        {editingProductId ? "Edit Product" : "Add Product"}
      </h1>

      <form
        onSubmit={handleSubmit}
        className="bg-white shadow rounded p-6 space-y-4"
      >
        <input
          type="text"
          value={form.name}
          placeholder="Product Name"
          onChange={(e) => setForm({ ...form, name: e.target.value })}
          className="w-full p-3 border border-gray-300 rounded focus:outline-none focus:ring-2 focus:ring-blue-500"
          required
        />
        <textarea
          value={form.description}
          placeholder="Description"
          onChange={(e) => setForm({ ...form, description: e.target.value })}
          className="w-full p-3 border border-gray-300 rounded focus:outline-none focus:ring-2 focus:ring-blue-500"
          required
        />
        <input
          type="url"
          value={form.imageUrl}
          placeholder="Image URL"
          onChange={(e) => setForm({ ...form, imageUrl: e.target.value })}
          className="w-full p-3 border border-gray-300 rounded focus:outline-none focus:ring-2 focus:ring-blue-500"
          required
        />
        <input
          type="number"
          value={form.price}
          placeholder="Price"
          onChange={(e) => setForm({ ...form, price: e.target.value })}
          className="w-full p-3 border border-gray-300 rounded focus:outline-none focus:ring-2 focus:ring-blue-500"
          required
        />

        <select
          value={form.category}
          onChange={(e) => {
            if (e.target.value === "add_new") {
              setShowCategoryInput(true);
              setForm({ ...form, category: "" });
            } else {
              setForm({ ...form, category: e.target.value });
              setShowCategoryInput(false);
              setNewCategoryName("");
            }
          }}
          className="w-full p-3 border border-gray-300 rounded bg-white focus:outline-none focus:ring-2 focus:ring-blue-500"
          required
        >
          <option value="">Select Category</option>
          {categories.map((cat) => (
            <option key={cat._id} value={cat._id}>
              {cat.name}
            </option>
          ))}
          <option value="add_new">+ Add new category...</option>
        </select>

        {showCategoryInput && (
          <div className="flex gap-2">
            <input
              type="text"
              value={newCategoryName}
              onChange={(e) => setNewCategoryName(e.target.value)}
              placeholder="New Category Name"
              className="flex-1 p-3 border border-gray-300 rounded focus:outline-none focus:ring-2 focus:ring-green-500"
            />
            <button
              type="button"
              onClick={handleAddCategory}
              className="bg-green-600 hover:bg-green-700 text-white px-4 py-2 rounded"
            >
              Save
            </button>
            <button
              type="button"
              onClick={() => {
                setShowCategoryInput(false);
                setNewCategoryName("");
              }}
              className="bg-gray-400 hover:bg-gray-500 text-white px-4 py-2 rounded"
            >
              Cancel
            </button>
          </div>
        )}

        <div className="flex gap-4">
          <button
            type="submit"
            className="bg-blue-600 hover:bg-blue-700 text-white px-6 py-2 rounded"
          >
            {editingProductId ? "Update Product" : "Add Product"}
          </button>
          {editingProductId && (
            <button
              type="button"
              onClick={resetForm}
              className="bg-gray-500 hover:bg-gray-600 text-white px-6 py-2 rounded"
            >
              Cancel Edit
            </button>
          )}
        </div>
      </form>

      <h2 className="text-2xl font-semibold mt-10 mb-2 text-gray-700">
        Filter Products
      </h2>
      <select
        onChange={(e) => setSelectedCategory(e.target.value)}
        className="w-full p-3 border border-gray-300 rounded mb-6 focus:outline-none focus:ring-2 focus:ring-blue-500"
      >
        <option value="">All Categories</option>
        {categories.map((cat) => (
          <option key={cat._id} value={cat._id}>
            {cat.name}
          </option>
        ))}
      </select>
 <h2 className="text-2xl font-semibold mt-10 mb-2 text-gray-700">
        Filter Products
      </h2>
      <div className="grid gap-6 grid-cols-1 sm:grid-cols-2 lg:grid-cols-3">
        {products
          .filter(
            (p) => !selectedCategory || p.category?._id === selectedCategory
          )
          .map((p) => (
            <div
              key={p._id}
              className="bg-white rounded shadow p-4 flex flex-col"
            >
              <img
                src={p.imageUrl}
                alt={p.name}
                className="w-full h-48 object-cover rounded mb-4"
              />
              <h3 className="text-lg font-bold mb-1 text-gray-800">{p.name}</h3>
              <p className="text-gray-600 flex-1">{p.description}</p>
              <div className="mt-2">
                <p className="text-blue-600 font-semibold">${p.price}</p>
                <p className="text-sm text-gray-500">{p.category?.name}</p>
              </div>
              <div className="flex gap-2 mt-4">
                <button
                  onClick={() => handleEdit(p)}
                  className="bg-yellow-500 hover:bg-yellow-600 text-white px-3 py-1 rounded"
                >
                  Edit
                </button>
                <button
                  onClick={() => handleDelete(p._id)}
                  className="bg-red-600 hover:bg-red-700 text-white px-3 py-1 rounded"
                >
                  Delete
                </button>
              </div>
            </div>
          ))}
      </div>
    </div>
  );
}

export default Product;
