import React, { useEffect, useState } from "react";

const API = "http://localhost:5000/api";

function App() {
  // --- Categories state ---
  const [categories, setCategories] = useState([]);
  const [categoryName, setCategoryName] = useState("");
  const [editingCategory, setEditingCategory] = useState(null);
  const [categoryError, setCategoryError] = useState("");

  // --- Products state ---
  const [products, setProducts] = useState([]);
  const [totalProducts, setTotalProducts] = useState(0);
  const [productPage, setProductPage] = useState(1);
  const [productLimit] = useState(9);
  const [filterCategory, setFilterCategory] = useState("");
  const [searchTerm, setSearchTerm] = useState("");

  // --- Product form state ---
  const [productForm, setProductForm] = useState({
    id: null,
    name: "",
    description: "",
    imageUrl: "",
    price: "",
    category: "",
  });
  const [productError, setProductError] = useState("");

  // --- Fetch categories ---
  async function fetchCategories() {
    try {
      const res = await fetch(`${API}/categories`);
      const data = await res.json();
      setCategories(data);
    } catch (e) {
      alert("Failed to fetch categories");
    }
  }

  // --- Fetch products ---
  async function fetchProducts() {
    try {
      const params = new URLSearchParams();
      params.append("page", productPage);
      params.append("limit", productLimit);
      if (filterCategory) params.append("category", filterCategory);
      if (searchTerm.trim()) params.append("search", searchTerm.trim());

      const res = await fetch(`${API}/products?${params.toString()}`);
      const data = await res.json();
      setProducts(data.products);
      setTotalProducts(data.total);
    } catch (e) {
      alert("Failed to fetch products");
    }
  }

  useEffect(() => {
    fetchCategories();
  }, []);

  useEffect(() => {
    fetchProducts();
  }, [productPage, filterCategory, searchTerm]);

  // --- Category handlers ---

  function resetCategoryForm() {
    setCategoryName("");
    setEditingCategory(null);
    setCategoryError("");
  }

  async function addOrEditCategory(e) {
    e.preventDefault();
    if (!categoryName.trim()) {
      setCategoryError("Category name cannot be empty");
      return;
    }

    try {
      if (editingCategory) {
        // Edit category
        const res = await fetch(`${API}/categories/${editingCategory._id}`, {
          method: "PUT",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({ name: categoryName.trim() }),
        });
        const data = await res.json();
        if (!res.ok) throw new Error(data.message || "Failed to update category");
      } else {
        // Add category
        const res = await fetch(`${API}/categories`, {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({ name: categoryName.trim() }),
        });
        const data = await res.json();
        if (!res.ok) throw new Error(data.message || "Failed to add category");
      }
      await fetchCategories();
      resetCategoryForm();
    } catch (err) {
      setCategoryError(err.message);
    }
  }

  async function editCategory(cat) {
    setEditingCategory(cat);
    setCategoryName(cat.name);
    setCategoryError("");
  }

  async function deleteCategory(cat) {
    if (!window.confirm(`Delete category "${cat.name}"?`)) return;

    try {
      const res = await fetch(`${API}/categories/${cat._id}`, { method: "DELETE" });
      const data = await res.json();
      if (!res.ok) throw new Error(data.message || "Failed to delete category");
      await fetchCategories();
      if (filterCategory === cat._id) setFilterCategory("");
    } catch (err) {
      alert(err.message);
    }
  }

  // --- Product form handlers ---

  function resetProductForm() {
    setProductForm({
      id: null,
      name: "",
      description: "",
      imageUrl: "",
      price: "",
      category: "",
    });
    setProductError("");
  }

  function handleProductChange(e) {
    const { name, value } = e.target;
    setProductForm((f) => ({ ...f, [name]: value }));
  }

  async function addOrEditProduct(e) {
    e.preventDefault();

    const { id, name, description, imageUrl, price, category } = productForm;

    if (!name.trim()) {
      setProductError("Product name is required");
      return;
    }
    if (!category) {
      setProductError("Category is required");
      return;
    }
    if (price === "" || isNaN(price) || Number(price) < 0) {
      setProductError("Valid price is required");
      return;
    }

    const body = {
      name: name.trim(),
      description: description.trim(),
      imageUrl: imageUrl.trim(),
      price: Number(price),
      category,
    };

    try {
      let res, data;
      if (id) {
        res = await fetch(`${API}/products/${id}`, {
          method: "PUT",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify(body),
        });
        data = await res.json();
        if (!res.ok) throw new Error(data.message || "Failed to update product");
      } else {
        res = await fetch(`${API}/products`, {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify(body),
        });
        data = await res.json();
        if (!res.ok) throw new Error(data.message || "Failed to add product");
      }
      resetProductForm();
      fetchProducts();
    } catch (err) {
      setProductError(err.message);
    }
  }

  function editProduct(product) {
    setProductForm({
      id: product._id,
      name: product.name,
      description: product.description,
      imageUrl: product.imageUrl,
      price: product.price,
      category: product.category._id,
    });
    setProductError("");
  }

  async function deleteProduct(product) {
    if (!window.confirm(`Delete product "${product.name}"?`)) return;

    try {
      const res = await fetch(`${API}/products/${product._id}`, { method: "DELETE" });
      const data = await res.json();
      if (!res.ok) throw new Error(data.message || "Failed to delete product");
      fetchProducts();
    } catch (err) {
      alert(err.message);
    }
  }

  // --- Pagination helpers ---
  const totalPages = Math.ceil(totalProducts / productLimit);

  return (
    <div style={{ maxWidth: 1200, margin: "auto", padding: 20, fontFamily: "Arial, sans-serif" }}>
      <h1>Product & Category Management</h1>

      {/* Categories Section */}
      <section style={{ marginBottom: 40 }}>
        <h2>Categories</h2>
        <form onSubmit={addOrEditCategory} style={{ marginBottom: 20 }}>
          <input
            type="text"
            placeholder="Category name"
            value={categoryName}
            onChange={(e) => setCategoryName(e.target.value)}
            style={{ padding: 8, fontSize: 16, width: 250 }}
          />
          <button type="submit" style={{ marginLeft: 10, padding: "8px 16px" }}>
            {editingCategory ? "Update" : "Add"}
          </button>
          {editingCategory && (
            <button
              type="button"
              onClick={resetCategoryForm}
              style={{ marginLeft: 10, padding: "8px 16px" }}
            >
              Cancel
            </button>
          )}
          {categoryError && <div style={{ color: "red", marginTop: 5 }}>{categoryError}</div>}
        </form>

        <ul>
          {categories.map((cat) => (
            <li key={cat._id} style={{ marginBottom: 6 }}>
              {cat.name}{" "}
              <button onClick={() => editCategory(cat)} style={{ marginLeft: 10 }}>
                Edit
              </button>{" "}
              <button onClick={() => deleteCategory(cat)} style={{ marginLeft: 5 }}>
                Delete
              </button>
            </li>
          ))}
        </ul>
      </section>

      {/* Products Section */}
      <section>
        <h2>Products</h2>

        {/* Filters */}
        <div style={{ marginBottom: 20 }}>
          <select
            value={filterCategory}
            onChange={(e) => {
              setProductPage(1);
              setFilterCategory(e.target.value);
            }}
            style={{ padding: 8, fontSize: 16, marginRight: 10 }}
          >
            <option value="">All Categories</option>
            {categories.map((cat) => (
              <option key={cat._id} value={cat._id}>
                {cat.name}
              </option>
            ))}
          </select>

          <input
            type="search"
            placeholder="Search products..."
            value={searchTerm}
            onChange={(e) => {
              setProductPage(1);
              setSearchTerm(e.target.value);
            }}
            style={{ padding: 8, fontSize: 16, width: 250 }}
          />
        </div>

        {/* Product Form */}
        <form onSubmit={addOrEditProduct} style={{ marginBottom: 30, maxWidth: 600 }}>
          <h3>{productForm.id ? "Edit Product" : "Add Product"}</h3>
          <div style={{ marginBottom: 10 }}>
            <input
              name="name"
              placeholder="Product name"
              value={productForm.name}
              onChange={handleProductChange}
              style={{ width: "100%", padding: 8, fontSize: 16 }}
            />
          </div>
          <div style={{ marginBottom: 10 }}>
            <textarea
              name="description"
              placeholder="Description"
              value={productForm.description}
              onChange={handleProductChange}
              rows={3}
              style={{ width: "100%", padding: 8, fontSize: 16 }}
            />
          </div>
          <div style={{ marginBottom: 10 }}>
            <input
              name="imageUrl"
              placeholder="Image URL"
              value={productForm.imageUrl}
              onChange={handleProductChange}
              style={{ width: "100%", padding: 8, fontSize: 16 }}
            />
          </div>
          <div style={{ marginBottom: 10 }}>
            <input
              name="price"
              placeholder="Price"
              type="number"
              step="0.01"
              min="0"
              value={productForm.price}
              onChange={handleProductChange}
              style={{ width: "100%", padding: 8, fontSize: 16 }}
            />
          </div>
          <div style={{ marginBottom: 10 }}>
            <select
              name="category"
              value={productForm.category}
              onChange={handleProductChange}
              style={{ width: "100%", padding: 8, fontSize: 16 }}
            >
              <option value="">Select Category</option>
              {categories.map((cat) => (
                <option key={cat._id} value={cat._id}>
                  {cat.name}
                </option>
              ))}
            </select>
          </div>

          {productError && <div style={{ color: "red", marginBottom: 10 }}>{productError}</div>}

          <button type="submit" style={{ padding: "8px 16px" }}>
            {productForm.id ? "Update Product" : "Add Product"}
          </button>
          {productForm.id && (
            <button
              type="button"
              onClick={resetProductForm}
              style={{ marginLeft: 10, padding: "8px 16px" }}
            >
              Cancel
            </button>
          )}
        </form>

        {/* Products List */}
        <div
          style={{
            display: "grid",
            gridTemplateColumns: "repeat(auto-fill,minmax(250px,1fr))",
            gap: 20,
          }}
        >
          {products.length === 0 && <p>No products found.</p>}

          {products.map((product) => (
            <div
              key={product._id}
              style={{
                border: "1px solid #ccc",
                borderRadius: 6,
                padding: 10,
                boxShadow: "1px 1px 5px rgba(0,0,0,0.1)",
              }}
            >
              {product.imageUrl ? (
                <img
                  src={product.imageUrl}
                  alt={product.name}
                  style={{ width: "100%", height: 150, objectFit: "cover", borderRadius: 4 }}
                />
              ) : (
                <div
                  style={{
                    width: "100%",
                    height: 150,
                    background: "#eee",
                    display: "flex",
                    alignItems: "center",
                    justifyContent: "center",
                    borderRadius: 4,
                    color: "#999",
                    fontStyle: "italic",
                  }}
                >
                  No image
                </div>
              )}

              <h3>{product.name}</h3>
              <p style={{ fontSize: 14, color: "#555" }}>{product.description || "No description"}</p>
              <p>
                <b>Category:</b> {product.category?.name || "Unknown"}
              </p>
              <p>
                <b>Price:</b> ${product.price.toFixed(2)}
              </p>

              <button onClick={() => editProduct(product)} style={{ marginRight: 10 }}>
                Edit
              </button>
              <button onClick={() => deleteProduct(product)}>Delete</button>
            </div>
          ))}
        </div>

        {/* Pagination */}
        {totalPages > 1 && (
          <div style={{ marginTop: 30 }}>
            <button
              onClick={() => setProductPage((p) => Math.max(1, p - 1))}
              disabled={productPage === 1}
              style={{ marginRight: 10 }}
            >
              Prev
            </button>
            <span>
              Page {productPage} of {totalPages}
            </span>
            <button
              onClick={() => setProductPage((p) => Math.min(totalPages, p + 1))}
              disabled={productPage === totalPages}
              style={{ marginLeft: 10 }}
            >
              Next
            </button>
          </div>
        )}
      </section>
    </div>
  );
}

export default App;
