import { useEffect, useState } from "react";

function Product() {
  const [products, setProducts] = useState([]);
  const [categories, setCategories] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState("");

  const [categoryForm, setCategoryForm] = useState({ name: "" });

  const [productForm, setProductForm] = useState({
    name: "",
    description: "",
    imageUrl: "",
    price: "",
    category: "",
  });

  // Fetch products and categories on mount
  useEffect(() => {
    fetchProducts();
    fetchCategories();
  }, []);

  const fetchProducts = async () => {
    try {
      const res = await fetch("http://localhost:5000/api/products");
      const data = await res.json();
      setProducts(data);
    } catch (err) {
      console.error("Error fetching products:", err);
    }
  };

  const fetchCategories = async () => {
    try {
      const res = await fetch("http://localhost:5000/api/categories");
      const data = await res.json();
      setCategories(data);
    } catch (err) {
      console.error("Error fetching categories:", err);
    }
  };

  // Add a new category
  const addCategory = async (e) => {
    e.preventDefault();
    if (!categoryForm.name.trim()) return;

    try {
      const res = await fetch("http://localhost:5000/api/categories", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(categoryForm),
      });

      if (res.ok) {
        const newCategory = await res.json();
        setCategories([...categories, newCategory]);
        setCategoryForm({ name: "" });
      } else {
        console.error("Failed to add category");
      }
    } catch (error) {
      console.error("Error adding category:", error);
    }
  };

  // Add a new product
  const addProduct = async (e) => {
    e.preventDefault();
    if (!productForm.name.trim() || !productForm.category) return;

    try {
      const res = await fetch("http://localhost:5000/api/products", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(productForm),
      });

      if (res.ok) {
        const newProduct = await res.json();
        setProducts([...products, newProduct]);
        setProductForm({
          name: "",
          description: "",
          imageUrl: "",
          price: "",
          category: "",
        });
      } else {
        console.error("Failed to add product");
      }
    } catch (error) {
      console.error("Error adding product:", error);
    }
  };

  const deleteProduct = async (id) => {
    if (!window.confirm("Are you sure?")) return;

    try {
      await fetch(`http://localhost:5000/api/products/${id}`, {
        method: "DELETE",
      });
      setProducts(products.filter((p) => p._id !== id));
    } catch (error) {
      console.error("Error deleting product:", error);
    }
  };

  return (
    <div className="p-6 max-w-4xl mx-auto">
      {/* ===== Add Category Form ===== */}
      <h1 className="text-2xl font-bold mb-4">Add Category</h1>
      <form onSubmit={addCategory} className="mb-8 space-y-2">
        <input
          type="text"
          placeholder="Category Name"
          value={categoryForm.name}
          onChange={(e) => setCategoryForm({ name: e.target.value })}
          className="w-full p-2 border rounded"
          required
        />
        <button type="submit" className="bg-green-600 text-white px-4 py-2 rounded">
          Add Category
        </button>
      </form>

      {/* ===== Add Product Form ===== */}
      <h1 className="text-2xl font-bold mb-4">Add Product</h1>
      <form onSubmit={addProduct} className="space-y-2 mb-6">
        <input
          type="text"
          placeholder="Product Name"
          value={productForm.name}
          onChange={(e) => setProductForm({ ...productForm, name: e.target.value })}
          className="w-full p-2 border rounded"
          required
        />
        <input
          type="text"
          placeholder="Description"
          value={productForm.description}
          onChange={(e) => setProductForm({ ...productForm, description: e.target.value })}
          className="w-full p-2 border rounded"
        />
        <input
          type="text"
          placeholder="Image URL"
          value={productForm.imageUrl}
          onChange={(e) => setProductForm({ ...productForm, imageUrl: e.target.value })}
          className="w-full p-2 border rounded"
        />
        <input
          type="number"
          placeholder="Price"
          value={productForm.price}
          onChange={(e) => setProductForm({ ...productForm, price: e.target.value })}
          className="w-full p-2 border rounded"
          min="0"
          step="0.01"
        />
        <select
          value={productForm.category}
          onChange={(e) => setProductForm({ ...productForm, category: e.target.value })}
          className="w-full p-2 border rounded"
          required
        >
          <option value="">Select Category</option>
          {categories.map((cat) => (
            <option key={cat._id} value={cat._id}>
              {cat.name}
            </option>
          ))}
        </select>
        <button className="bg-blue-600 text-white px-4 py-2 rounded">
          Add Product
        </button>
      </form>

      {/* ===== Filter Products by Category ===== */}
      <h2 className="text-xl font-semibold mb-2">Filter Products</h2>
      <select
        onChange={(e) => setSelectedCategory(e.target.value)}
        value={selectedCategory}
        className="w-full p-2 border rounded mb-4"
      >
        <option value="">All</option>
        {categories.map((cat) => (
          <option key={cat._id} value={cat._id}>
            {cat.name}
          </option>
        ))}
      </select>

      {/* ===== Display Products ===== */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
        {products
          .filter((p) => !selectedCategory || p.category?._id === selectedCategory)
          .map((p) => (
            <div key={p._id} className="border p-4 rounded shadow">
              {p.imageUrl && (
                <img
                  src={p.imageUrl}
                  alt={p.name}
                  className="w-full h-40 object-cover mb-2 rounded"
                />
              )}
              <h3 className="font-bold text-lg">{p.name}</h3>
              <p>{p.description}</p>
              <p className="font-semibold">${p.price}</p>
              <p className="text-sm text-gray-500">{p.category?.name}</p>
              <button
                onClick={() => deleteProduct(p._id)}
                className="mt-2 bg-red-500 text-white px-3 py-1 rounded"
              >
                Delete
              </button>
            </div>
          ))}
      </div>
    </div>
  );
}

export default Product;
