import { useEffect, useState } from "react";

function Product() {
  const [products, setProducts] = useState([]);
  const [categories, setCategories] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState("");

  // Form state for adding products
  const [form, setForm] = useState({
    name: "",
    description: "",
    imageUrl: "",
    price: "",
    category: "",
  });

  // Form state for adding categories
  const [categoryForm, setCategoryForm] = useState({
    name: "",
  });

  // Fetch products and categories on mount
  useEffect(() => {
    fetchProducts();
    fetchCategories();
  }, []);

  const fetchProducts = () => {
    fetch("http://localhost:5000/api/products")
      .then((res) => res.json())
      .then(setProducts)
      .catch(console.error);
  };

  const fetchCategories = () => {
    fetch("http://localhost:5000/api/categories")
      .then((res) => res.json())
      .then(setCategories)
      .catch(console.error);
  };

  // Add product
  const addProduct = async (e) => {
    e.preventDefault();
    if (!form.category) {
      alert("Please select a category");
      return;
    }
    const res = await fetch("http://localhost:5000/api/products", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        ...form,
        price: parseFloat(form.price),
      }),
    });
    if (res.ok) {
      const newProduct = await res.json();
      setProducts([...products, newProduct]);
      setForm({
        name: "",
        description: "",
        imageUrl: "",
        price: "",
        category: "",
      });
    } else {
      alert("Failed to add product");
    }
  };

  // Delete product
  const deleteProduct = async (id) => {
    if (!window.confirm("Are you sure?")) return;
    const res = await fetch(`http://localhost:5000/api/products/${id}`, {
      method: "DELETE",
    });
    if (res.ok) {
      setProducts(products.filter((p) => p._id !== id));
    }
  };

  // Add category
  const addCategory = async (e) => {
    e.preventDefault();
    if (!categoryForm.name.trim()) {
      alert("Category name required");
      return;
    }
    const res = await fetch("http://localhost:5000/api/categories", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(categoryForm),
    });
    if (res.ok) {
      const newCat = await res.json();
      setCategories([...categories, newCat]);
      setCategoryForm({ name: "" });
    } else {
      alert("Failed to add category");
    }
  };

  // Delete category only if no products assigned
  const deleteCategory = async (id) => {
    if (!window.confirm("Delete this category? Only allowed if no products belong to it.")) return;
    const res = await fetch(`http://localhost:5000/api/categories/${id}`, {
      method: "DELETE",
    });
    if (res.ok) {
      setCategories(categories.filter((c) => c._id !== id));
    } else {
      const data = await res.json();
      alert(data.message || "Failed to delete category");
    }
  };

  // Filter products by category
  const filteredProducts = products.filter(
    (p) => !selectedCategory || (p.category && p.category._id === selectedCategory)
  );

  return (
    <div className="p-6 max-w-4xl mx-auto">
      <h1 className="text-2xl font-bold mb-4">Add Category</h1>
      <form onSubmit={addCategory} className="flex mb-6 space-x-2">
        <input
          type="text"
          placeholder="Category name"
          value={categoryForm.name}
          onChange={(e) => setCategoryForm({ name: e.target.value })}
          className="flex-grow p-2 border rounded"
        />
        <button className="bg-green-600 text-white px-4 py-2 rounded">Add</button>
      </form>

      <h2 className="text-xl font-semibold mb-2">Categories</h2>
      <ul className="mb-6">
        {categories.map((cat) => (
          <li key={cat._id} className="flex justify-between items-center mb-1 border p-2 rounded">
            <span>{cat.name}</span>
            <button
              onClick={() => deleteCategory(cat._id)}
              className="text-red-600 hover:text-red-800"
            >
              Delete
            </button>
          </li>
        ))}
      </ul>

      <h1 className="text-2xl font-bold mb-4">Add Product</h1>
      <form onSubmit={addProduct} className="space-y-2 mb-6">
        <input
          type="text"
          value={form.name}
          placeholder="Name"
          onChange={(e) => setForm({ ...form, name: e.target.value })}
          className="w-full p-2 border rounded"
          required
        />
        <input
          type="text"
          value={form.description}
          placeholder="Description"
          onChange={(e) => setForm({ ...form, description: e.target.value })}
          className="w-full p-2 border rounded"
        />
        <input
          type="text"
          value={form.imageUrl}
          placeholder="Image URL"
          onChange={(e) => setForm({ ...form, imageUrl: e.target.value })}
          className="w-full p-2 border rounded"
        />
        <input
          type="number"
          value={form.price}
          placeholder="Price"
          onChange={(e) => setForm({ ...form, price: e.target.value })}
          className="w-full p-2 border rounded"
          min="0"
          step="0.01"
          required
        />
        <select
          value={form.category}
          onChange={(e) => setForm({ ...form, category: e.target.value })}
          className="w-full p-2 border rounded"
          required
        >
          <option value="">Select Category</option>
          {categories.map((cat) => (
            <option key={cat._id} value={cat._id}>
              {cat.name}
            </option>
          ))}
        </select>
        <button className="bg-blue-600 text-white px-4 py-2 rounded">Add Product</button>
      </form>

      <h2 className="text-xl font-semibold mb-2">Filter Products</h2>
      <select
        onChange={(e) => setSelectedCategory(e.target.value)}
        className="w-full p-2 border rounded mb-4"
        value={selectedCategory}
      >
        <option value="">All</option>
        {categories.map((cat) => (
          <option key={cat._id} value={cat._id}>
            {cat.name}
          </option>
        ))}
      </select>

      <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
        {filteredProducts.map((p) => (
          <div key={p._id} className="border p-4 rounded shadow">
            {p.imageUrl && (
              <img
                src={p.imageUrl}
                alt={p.name}
                className="w-full h-40 object-cover mb-2 rounded"
              />
            )}
            <h3 className="font-bold text-lg">{p.name}</h3>
            <p>{p.description}</p>
            <p className="font-semibold">${p.price.toFixed(2)}</p>
            <p className="text-sm text-gray-500">{p.category?.name}</p>
            <button
              onClick={() => deleteProduct(p._id)}
              className="mt-2 bg-red-500 text-white px-3 py-1 rounded"
            >
              Delete
            </button>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Product;
