import React, { useEffect, useState } from "react";

function Product() {
  const [products, setProducts] = useState([]);
  const [categories, setCategories] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState("");
  const [searchTerm, setSearchTerm] = useState("");
  const [editingProductId, setEditingProductId] = useState(null);
  const [editingCategoryId, setEditingCategoryId] = useState(null);
  const [categoryInput, setCategoryInput] = useState("");
  const [productForm, setProductForm] = useState({
    name: "",
    description: "",
    imageUrl: "",
    price: "",
    categoryId: "",
  });

  useEffect(() => {
    fetchProducts();
    fetchCategories();
  }, []);

  async function fetchProducts() {
    try {
      const res = await fetch("http://localhost:5000/api/products");
      const data = await res.json();
      setProducts(data);
    } catch (err) {
      console.error("Failed to fetch products", err);
    }
  }

  async function fetchCategories() {
    try {
      const res = await fetch("http://localhost:5000/api/categories");
      const data = await res.json();
      setCategories(data);
    } catch (err) {
      console.error("Failed to fetch categories", err);
    }
  }

  function resetProductForm() {
    setProductForm({
      name: "",
      description: "",
      imageUrl: "",
      price: "",
      categoryId: "",
    });
    setEditingProductId(null);
  }

  async function handleProductSubmit(e) {
    e.preventDefault();
    try {
      const url = editingProductId
        ? `http://localhost:5000/api/products/${editingProductId}`
        : "http://localhost:5000/api/products";
      const method = editingProductId ? "PUT" : "POST";

      const res = await fetch(url, {
        method,
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          name: productForm.name,
          description: productForm.description,
          imageUrl: productForm.imageUrl,
          price: Number(productForm.price),
          category: productForm.categoryId,
        }),
      });

      if (!res.ok) {
        throw new Error("Failed to save product");
      }

      const savedProduct = await res.json();

      if (editingProductId) {
        setProducts((prev) =>
          prev.map((p) => (p._id === editingProductId ? savedProduct : p))
        );
      } else {
        setProducts((prev) => [...prev, savedProduct]);
      }

      resetProductForm();
    } catch (err) {
      alert(err.message);
    }
  }

  async function handleDeleteProduct(id) {
    if (!window.confirm("Are you sure you want to delete this product?"))
      return;

    try {
      const res = await fetch(`http://localhost:5000/api/products/${id}`, {
        method: "DELETE",
      });
      if (!res.ok) throw new Error("Delete failed");

      setProducts((prev) => prev.filter((p) => p._id !== id));
    } catch {
      alert("Could not delete product.");
    }
  }

  function startEditProduct(product) {
    setProductForm({
      name: product.name,
      description: product.description,
      imageUrl: product.imageUrl,
      price: product.price,
      categoryId: product.category?._id || "",
    });
    setEditingProductId(product._id);
    window.scrollTo({ top: 0, behavior: "smooth" });
  }

  function resetCategoryForm() {
    setCategoryInput("");
    setEditingCategoryId(null);
  }

  async function handleCategorySubmit(e) {
    e.preventDefault();

    if (!categoryInput.trim()) {
      alert("Category name cannot be empty.");
      return;
    }

    try {
      const url = editingCategoryId
        ? `http://localhost:5000/api/categories/${editingCategoryId}`
        : "http://localhost:5000/api/categories";
      const method = editingCategoryId ? "PUT" : "POST";

      const res = await fetch(url, {
        method,
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ name: categoryInput }),
      });

      if (!res.ok) throw new Error("Failed to save category");

      if (editingCategoryId) {
        await fetchCategories();
        resetCategoryForm();
      } else {
        const newCat = await res.json();
        setCategories((prev) => [...prev, newCat]);
        setCategoryInput("");
      }
    } catch (err) {
      alert(err.message);
    }
  }

  function startEditCategory(cat) {
    setCategoryInput(cat.name);
    setEditingCategoryId(cat._id);
  }

  async function handleDeleteCategory(id) {
    if (!window.confirm("Delete this category? It cannot be undone.")) return;

    try {
      const res = await fetch(`http://localhost:5000/api/categories/${id}`, {
        method: "DELETE",
      });
      const data = await res.json();

      if (!res.ok) {
        alert(data.message || "Failed to delete category");
        return;
      }

      await fetchCategories();
    } catch {
      alert("Error deleting category.");
    }
  }

  const filteredProducts = products.filter((p) => {
    const matchesCategory =
      !selectedCategory || p.category?._id === selectedCategory;
    const matchesSearch =
      p.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
      p.description.toLowerCase().includes(searchTerm.toLowerCase());

    return matchesCategory && matchesSearch;
  });

  return (
    <div className="max-w-5xl mx-auto p-6">
      <h2 className="text-3xl font-semibold mb-6 text-center">
        Product Management System
      </h2>

      {/* Category Section */}
      <section className="mb-10">
        <h3 className="text-2xl font-semibold mb-3">
          {editingCategoryId ? "Edit Category" : "Add Category"}
        </h3>
        <form
          onSubmit={handleCategorySubmit}
          className="flex gap-3 mb-5 max-w-md"
        >
          <input
            type="text"
            value={categoryInput}
            onChange={(e) => setCategoryInput(e.target.value)}
            placeholder="Category name"
            className="flex-grow border border-gray-300 rounded-md px-4 py-2 focus:outline-none focus:ring-2 focus:ring-green-500"
          />
          <button
            type="submit"
            className="bg-green-600 text-white px-5 rounded-md hover:bg-green-700 transition"
          >
            {editingCategoryId ? "Update" : "Add"}
          </button>
          {editingCategoryId && (
            <button
              type="button"
              onClick={resetCategoryForm}
              className="bg-gray-400 text-white px-5 rounded-md hover:bg-gray-500 transition"
            >
              Cancel
            </button>
          )}
        </form>

        <ul className="space-y-2 max-w-md">
          {categories.map((cat) => (
            <li
              key={cat._id}
              className="flex justify-between items-center border border-gray-200 p-3 rounded-md shadow-sm"
            >
              <span className="font-medium">{cat.name}</span>
              <div className="flex gap-3">
                <button
                  onClick={() => startEditCategory(cat)}
                  className="text-yellow-600 hover:underline"
                >
                  Edit
                </button>
                <button
                  onClick={() => handleDeleteCategory(cat._id)}
                  className="text-red-600 hover:underline"
                >
                  Delete
                </button>
              </div>
            </li>
          ))}
        </ul>
      </section>

      {/* Product Form Section */}
      <section className="mb-10">
        <h3 className="text-2xl font-semibold mb-4">
          {editingProductId ? "Edit Product" : "Add Product"}
        </h3>
        <form onSubmit={handleProductSubmit} className="max-w-md space-y-4">
          <input
            type="text"
            value={productForm.name}
            onChange={(e) =>
              setProductForm({ ...productForm, name: e.target.value })
            }
            placeholder="Product Name"
            className="w-full border border-gray-300 rounded-md px-4 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
            required
          />
          <textarea
            value={productForm.description}
            onChange={(e) =>
              setProductForm({ ...productForm, description: e.target.value })
            }
            placeholder="Description"
            rows={4}
            className="w-full border border-gray-300 rounded-md px-4 py-2 resize-none focus:outline-none focus:ring-2 focus:ring-blue-500"
          />
          <input
            type="text"
            value={productForm.imageUrl}
            onChange={(e) =>
              setProductForm({ ...productForm, imageUrl: e.target.value })
            }
            placeholder="Image URL"
            className="w-full border border-gray-300 rounded-md px-4 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
          />
          <input
            type="number"
            value={productForm.price}
            onChange={(e) =>
              setProductForm({ ...productForm, price: e.target.value })
            }
            placeholder="Price"
            min="0"
            step="0.01"
            className="w-full border border-gray-300 rounded-md px-4 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
          />
          <select
            value={productForm.categoryId}
            onChange={(e) =>
              setProductForm({ ...productForm, categoryId: e.target.value })
            }
            required
            className="w-full border border-gray-300 rounded-md px-4 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
          >
            <option value="">Select Category</option>
            {categories.map((cat) => (
              <option key={cat._id} value={cat._id}>
                {cat.name}
              </option>
            ))}
          </select>

          <div className="flex gap-4">
            <button
              type="submit"
              className="bg-blue-600 text-white px-6 py-2 rounded-md hover:bg-blue-700 transition"
            >
              {editingProductId ? "Update Product" : "Add Product"}
            </button>
            {editingProductId && (
              <button
                type="button"
                onClick={resetProductForm}
                className="bg-gray-400 px-6 py-2 rounded-md hover:bg-gray-500 transition"
              >
                Cancel
              </button>
            )}
          </div>
        </form>
      </section>

      {/* Filter/Search Section */}
      <section className="mb-10 max-w-md">
        <h3 className="text-2xl font-semibold mb-4">Filter Products</h3>
        <select
          value={selectedCategory}
          onChange={(e) => setSelectedCategory(e.target.value)}
          className="w-full border border-gray-300 rounded-md px-4 py-2 focus:outline-none focus:ring-2 focus:ring-indigo-500"
        >
          <option value="">All Categories</option>
          {categories.map((cat) => (
            <option key={cat._id} value={cat._id}>
              {cat.name}
            </option>
          ))}
        </select>

        <input
          type="text"
          placeholder="Search products..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          className="mt-4 w-full border border-gray-300 rounded-md px-4 py-2 focus:outline-none focus:ring-2 focus:ring-indigo-500"
        />
      </section>

      {/* Product List */}
      <section>
        {filteredProducts.length === 0 ? (
          <p className="text-center text-gray-500">No products found.</p>
        ) : (
          <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
            {filteredProducts.map((product) => (
              <div
                key={product._id}
                className="border border-gray-200 rounded-md shadow-sm p-4 flex flex-col"
              >
                {product.imageUrl && (
                  <img
                    src={product.imageUrl}
                    alt={product.name}
                    className="mb-4 h-40 w-full object-cover rounded-md"
                  />
                )}
                <h4 className="font-semibold text-lg mb-1">{product.name}</h4>
                <p className="text-gray-700 text-sm flex-grow">
                  {product.description}
                </p>
                <p className="mt-3 font-semibold text-blue-700">
                  ${product.price.toFixed(2)}
                </p>
                <p className="text-xs text-gray-500 mt-1">
                  Category: {product.category?.name || "Uncategorized"}
                </p>

                <div className="mt-4 flex justify-between">
                  <button
                    onClick={() => startEditProduct(product)}
                    className="text-yellow-600 hover:underline"
                  >
                    Edit
                  </button>
                  <button
                    onClick={() => handleDeleteProduct(product._id)}
                    className="text-red-600 hover:underline"
                  >
                    Delete
                  </button>
                </div>
              </div>
            ))}
          </div>
        )}
      </section>
    </div>
  );
}

export default Product;
